INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Miya', 'Moon Blessing', 'passive', 'Moon BlessingAbilityBUFF Miya gains 5% extra Attack Speed for 4s each time she hits an enemy with her Basic Attack (up to 5 stacks). At full stacks, she summons Moonlight Shadow to aid her on each Basic Attack, dealing 30 (+30% Total Physical Attack) extra Physical Damage to the target. This damage can critically strike but cannot trigger attack effects.', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Miya', 'Moon Arrow', 'skill1', 'Moon ArrowAbilityBUFF AoETypePhysical Basic ATK Miya shoots two extra arrows on each Basic Attack, dealing 10 (+100% Total Physical Attack) Physical Damage to the target enemy and 30% damage to the rest. This effect lasts 4s. The extra arrows can trigger attack effects. 11.060', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Miya', 'Arrow of Eclipse', 'skill2', 'Arrow of EclipseAbilityCC AoETypePhysical Skill Miya launches an empowered arrow on the target area, dealing 90 (+45% Total Physical Attack) Physical Damage to enemies within and immobilizing them for 1.2s. The arrow then splits into 8 scattering minor arrows, each dealing 30 (+15% Total Physical Attack) Physical Damage to the first enemy hit and slowing them by 30% for 2s. 8.080', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Miya', 'Hidden Moonlight', 'ultimate', 'Hidden MoonlightAbilityConceal Remove CC Miya removes all debuffs on her and conceals herself, gaining 35% extra Movement Speed. This state lasts 2.0s or until she launches an attack. Miya gains full stacks of Moon Blessing upon leaving the state. 46.0120', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Eudora', 'Superconductor', 'passive', 'SuperconductorAbilityBUFF Eudora''s skills inflict Superconductor for 3s on hit and can trigger additional effects against enemies affected by Superconductor.', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Eudora', 'Forked Lightning', 'skill1', 'Forked LightningAbilityAoETypeMagic Skill Eudora casts fork lightning in a target direction, dealing 350(+150%Total Magic Power) Magic Damage to enemies in a fan-shaped area. Enemies affected by Superconductor will take 190(+30%Total Magic Power) extra Magic Damage after a short delay. 7.065', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Eudora', 'Ball Lightning', 'skill2', 'Ball LightningAbilityCC DamageTypeMagic Skill Eudora hurls an orb of lightning at the target enemy, dealing them 350(+80%Total Magic Power) Magic Damage, stunning them for 1.2s, and reducing their Magic Defense by 10 for 1.8s. If the enemy is affected by Superconductor, the lightning orb will bounce to up to 3 nearby enemies, dealing them 175(+40%Total Magic Power) Magic Damage and stunning them for 0.6s (prioritizes enemy heroes). 11.090', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Eudora', 'Thunder''s Wrath', 'ultimate', 'Thunder''s WrathAbilityBurstTypeMagic Skill Eudora calls down a blast of lightning on the target enemy, dealing them 480(+180%Total Magic Power) Magic Damage. If the enemy is affected by Superconductor, dark clouds will form over their head and send down an extra lightning strike on the area below after a 0.8s delay, dealing 440(+150%Total Magic Power) Magic Damage to enemies within and 360(+150%Total Magic Power) Magic Damage to nearby enemies. 32.0130', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Tigreal', 'Tigreal Passive', 'passive', 'Fearless Ability TypeBuff Tigreal gains a stack of Fearless each time he uses a skill or is hit by a Basic Attack. After getting 4 stacks, Tigreal will consume all Fearless stacks to block the next incoming Basic Attack (including attacks from Turrets)Attacks from Minions do not grant Fearless stacks nor trigger its effect', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Tigreal', 'Tigreal Skill1', 'skill1', 'Attack Wave Ability TypeAOE Slow DamagePhysical Base Cooldown: 7.0 Base Mana Cost: 45 Tigreal smashes the ground with his hammer and sends a shockwave that erupts 3 time in the target direction, each time deadling 270 (70%Total Physical Attack) Physical Damage to enemies along the fan-shaped area and slowing them by 20%/40%/60% for 1.5s', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Tigreal', 'Tigreal Skill2', 'skill2', 'Sacred Hammer Ability TypeCharge CC DamagePhysical Base Cooldown: 16.0 Base Mana Cost: 70 Tigreal charges in the target direction, dealing (+100%Total Physical Attack) Physical Damage to enemies along the way and pushing them to the end of the chargeUse Again: Tigreal can use this skill again within 4s, dealing 280 (+60%Total Physical Attack) Physical Damage to enemies in front of him and knocking them airborne for 1s.', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Tigreal', 'Tigreal Ultimate', 'ultimate', 'Implosion Ability TypeCC AOE DamagePhysical Base Cooldown: 55.0 Base Mana Cost: 120 Tigreal unleashes the power of his hammer, pulling nearby enemies to him and stunning them for 1.8s while dealing 270 (+130%Total Physical Attack) Physical DamageThe first half of the channeling can be interrupted by control effects. The second half of the channeling can only be interrupted by Suppression.', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Saber', 'Enemy''s Bane', 'passive', 'Enemy''s BaneAbilityDebuff Saber''s attacks reduce enemies'' Physical Defense by 3-8 for 5s on hit (up to 5 stacks).', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Saber', 'Orbiting Swords', 'skill1', 'Orbiting SwordsAbilityAoE BUFFTypePhysical Skill Saber releases 5 swords that orbit around him and deal 80(+30%Extra Physical Attack) Physical Damage to nearby enemies on hit. The swords will retract back to him after a while. When the swords are present, Saber sends a sword toward his target on each attack, dealing 210(+60%Extra Physical Attack) extra Physical Damage to the target and 50% of the damage to other enemies it passes through (damage against minions is reduced to 50% ). Each attack also reduces the cooldown of Charge by is. 9.075', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Saber', 'Charge', 'skill2', 'ChargeAbilityBlink AoETypePhysical Skill/Physical Basic ATK Saber dashes in the target direction, dealing 75(+50%Extra Physical Attack) Physical Damage to enemies along the way while enhancing his next Basic Attack. The enhanced Basic Attack deals 75(+120%Total Physical Attack) Physical Damage and slows the target by 60% for 1s. 12.070', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Saber', 'Triple Sweep', 'ultimate', 'Triple SweepAbilityBurst CCTypePhysical Skill/Blink Saber charges at the target enemy hero, knocking them airborne for 1.2s and striking them 3 times over the duration. The first two strikes deal 120(+100%Extra Physical Attack) Physical Damage each, while the third strike deals 240(+200%Extra Physical Attack) Physical Damage. 44.0100', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Alice', 'Alice Passive', 'passive', 'Blood Ancestry Ability TypeBuff Alice restores 1.5% Max Mana per second, which increases to 3% when in combat against enemy heroes.Each time Alice levels up, her Movement Speed increases by 3 and Healing Received increases by 1%.', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Alice', 'Alice Skill1', 'skill1', 'Flowing Blood Ability TypeMobility AOE DamageMagic Base Cooldown: 8.0 Base Mana Cost: 50 Alice casts a sphere of blood energy in the target direction, dealing 400 (+120%Total Magic Power) Magic Damage to enemies in its path.Use Again: Alice immediately teleports to the blood sphere''s location.', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Alice', 'Alice Skill2', 'skill2', 'Blood Awe Ability TypeCC AOE DamageMagic Base Cooldown: 6.0 Base Mana Cost: 100 Alice deals 200 (+75%Total Magic Power) plus 2.5 (+0.3%Total Mana)% of the target''s Max HP as Magic Damage to nearby enemies and immobilizes them for 0.5s. After the immobilizes effect ends, the target will be slowed by 70% for 1.5s.Creeps can only take up to 250 damage from the max HP-based damage.', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Alice', 'Alice Ultimate', 'ultimate', 'Blood Ode Ability TypeAOE Heal DamageMagic Base Cooldown: 2.0 Base Mana Cost: 60 Alice enters the blood-draining state, consuming 60 Mana while dealing 30 (+30%Total Magic Power) Magic Damage to nearby enemies every 0.5s. She recovers 40 (+30% Total Magic Power) HP each time she damages an enemy hero (only 50% of that HP for other enemy units).Each time this effect damages an enemy hero, the cooldown of Flowing Blood is reduced by 0.2s and the cooldown of Blood Owe is reduced by 0.5%.', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Nana', 'Molina''s Gift', 'passive', 'Molina''s GiftAbilityDeath Immune Upon taking fatal damage, Nana removes all debuffs on her and transforms, becoming untargetable and invincible and gaining 70% extra Movement Speed for 2s. She also recovers 10% Max HP over the duration. Nana can only transform once every 150s and cannot cast skills in the transformed form.', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Nana', 'Magic Boomerang', 'skill1', 'Magic BoomerangAbilityAoETypeMagic Skill Nana hurls her Magic Boomerang in the target direction, dealing 245 (+120 % Total Magic Power) Magic Damage to enemies hit on its way out and back. This damage is reduced by 20% for each extra enemy hit (up to 60%). 5.550', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Nana', 'Molina Smooch', 'skill2', 'Molina SmoochAbilityCC DebuffTypeMagic Skill Nana sends Molina to the target location, who will then seek and chase the nearest enemy hero in the area and transform the first enemy hero it collides with for 1.5s. Molina also deals 250 (+50% Total Magic Power) Magic Damage to the transformed enemy, slows them by 50%, and reduces their Magic Defense by 25% for the duration. Molina will remain stationary for up to 12s if there are no enemy heroes in the area and can chase an enemy hero for up to 2.5s before it disappears. 14.580', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Nana', 'Molina Blitz', 'ultimate', 'Molina BlitzAbilityBurst CCTypeMagic Skill Nana summons Molina to attack the target area 3 times, each time dealing 400 (+180% Total Magic Power) Magic Damage to enemies within and slowing them by 50% for 1s. Enemies hit twice will be stunned for 1s. 36.0135', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Fanny', 'Air Superiority', 'passive', 'Air SuperiorityAbilityBUFF During flight, Fanny deals 15%-30% extra damage to enemies hit (scales with flying speed) and applies a stack of Prey Mark to them (up to 2 stacks) on each attack. Her subsequent skill hits on marked enemy heroes restore 8 energy per stack for her. The energy restoration is reduced if she hits multiple enemy heroes within a short period of time.', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Fanny', 'Tornado Strike', 'skill1', 'Tornado StrikeAbilityAoETypePhysical Skill Fanny whirls her blades, dealing 320(+80%Total Physical Attack) Physical Damage to nearby enemies. 3.515', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Fanny', 'Steal Cable', 'skill2', 'Steal CableAbilityBlinkTypeCharge Fanny shoots a cable in the target direction that pulls her to the first obstacle hit. She can cast this skill again within 2s until her energy runs out. Each successive cast reduces the skill''s energy cost by 2. As long as her energy is sufficient, Fanny automatically casts Tornado Strike upon hitting an enemy mid-flight. 0.017No way I''m going on foot!', NULL, NULL);
