INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Layla', 'Malefic Gun', 'passive', 'Malefic GunAbilityBUFF Layla deals increased damage to enemies farther away from her (from 100% to 140%, reaches the maximum at 6 units). Target locked. Fire!', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Layla', 'Malefic Bomb', 'skill1', 'Malefic BombAbilityBurst BUFFTypePhysical Skill Layla fires a Malefic Energy Bomb in the target direction, dealing 200 (+80% Total Physical Attack) Physical Damage to the first enemy hit. This damage can critically strike. Upon hitting an enemy, Layla gains extra Basic Attack range for 3s and 60% extra Movement Speed that decays over 1.2s. The duration of the Movement Speed boost is doubled if an enemy hero is hit. 6.040', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Layla', 'Void Projectile', 'skill2', 'Void ProjectileAbilityAoE SlowTypePhysical Skill Layla fires an orb of Malefic Energy at the target enemy that explodes on hit, dealing 170 (+65% Total Physical Attack) Physical Damage to enemies in the area, slowing them by 30% for 1.2s, and marking them with a Malefic Mark. Layla''s subsequent attacks will detonate the mark, dealing 100 (+35% Total Physical Attack) extra Physical Damage to enemies in the area and slowing them by 30% for 1.2s. 7.565', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Layla', 'Destruction Rush', 'ultimate', 'Destruction RushAbilityBurst BUFFTypePhysical Skill Layla fires a blast of Malefic Energy in the target direction, dealing 500 (+150% Total Physical Attack) Physical Damage to enemies in a line. Passive: The range of Layla''s Void Projectile and Basic Attacks is increased by 0.6 units. Her sight range is also slightly increased each time this skill is upgraded. 37.0130', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Rafaela', 'Deity Penalization', 'passive', 'Deity PenalizationAbilityBurst Upon death, Rafaela turns into a beam of Holy Light and launches herself at her killer after a 2s delay, dealing them True Damage equal to 20% of their Max HP. The attack can be blocked by other enemy heroes. This effect won''t be triggered if the killer is a non-hero enemy or if they''re too far away.', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Rafaela', 'Light of Retribution', 'skill1', 'Light of RetributionAbilityAoE SlowTypeMagic Skill Rafaela strikes three nearest enemies with Light of Retribution, dealing them 225(+120%Total Magic Power) Magic Damage, briefly revealing their positions, and slowing them by 40% for 1.5s. Enemies hit by Light of Retribution again within 5s will take 20% extra damage (up to 3 stacks). 4.070', NULL, NULL);
INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Rafaela', 'Holy Healing', 'skill2', 'Holy HealingAbilityHEAL Speed UpTypeHeal Rafaela calls upon Holy Light, restoring 250(+50%Total Magic Power) HP for herself and the most injured allied hero around her, 100(+25%Total Magic Power) HP for other nearby allied heroes, while granting herself and nearby allied heroes 50% extra Movement Speed for 1.5s. 10.5100', NULL, NULL);
