INSERT OR REPLACE INTO abilities (hero_name, name, type, description, cooldown, mana_cost) VALUES ('Bane', 'Bane Passive', 'passive', 'Shark Bite Ability TypeBurst Bane infuses his weapon with a stack of Tidal Energy each time he uses a skill (up to 2 stacks). Each of his subsequent Basic Attacks consumes a stack to deal Physical Damage equal to (+95%Total Physical Attack) plus 4(+0.4*Hero Level)% of the target''s Max HP to nearby enemies.', NULL, NULL);
